import { useEffect, useState } from 'react';
import '../App.css';
import axios from 'axios';
import { auth } from '../firebase';
import { useNavigate, Link } from 'react-router-dom'; // Import Link from react-router-dom
import { signInWithGoogle, getCurrentUser } from './Login';

const Rating = ({ rating, onRate, numRatings, averageRating }) => {
  const [hoverRating, setHoverRating] = useState(0);

  return (
    <div className="rating">
      <div>
        {[1, 2, 3, 4, 5].map((value) => (
          <span
            key={value}
            className={`star ${value <= (hoverRating || rating) ? 'filled' : ''}`}
            onClick={() => onRate(value)}
            onMouseEnter={() => setHoverRating(value)}
            onMouseLeave={() => setHoverRating(0)}
          >
            â˜…
          </span>
        ))}
      </div>
      <div className="rating-info">
        <p>{numRatings} rating{numRatings !== 1 ? 's' : ''}</p>
        <p>Average: {averageRating.toFixed(1)}</p>
      </div>
    </div>
  );
};

const App = () => {
  const [myData, setMyData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [isError, setIsError] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const [ratings, setRatings] = useState({});
  const [user, setUser] = useState(getCurrentUser());
  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged(user => {
      setUser(user);
    });

    return () => unsubscribe();
  }, []);

  useEffect(() => {
    axios
      .get("https://raw.githubusercontent.com/ayushku634/ayushku634/main/data.json")
      .then((response) => {
        setMyData(response.data);
        if (response.data.length > 0) {
          setSelectedCategory(response.data[0].cat_id.toString());
        }
        setIsLoading(false);

        const storedRatings = JSON.parse(localStorage.getItem('ratings')) || {};
        setRatings(storedRatings);
      })
      .catch((error) => {
        setIsError(error.message);
        setIsLoading(false);
      });
  }, []);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleRate = async (itemId, rating) => {
    if (!user) {
      try {
        const loggedInUser = await signInWithGoogle();
        setUser(loggedInUser);
        rateItem(itemId, rating, loggedInUser.email);
      } catch (error) {
        console.error("Error during sign in:", error);
      }
    } else {
      rateItem(itemId, rating, user.email);
    }
  };

  const rateItem = (itemId, rating, userEmail) => {
    setRatings((prevRatings) => {
      const currentRatings = prevRatings[itemId] || { count: 0, sum: 0, users: [] };
      if (currentRatings.users.includes(userEmail)) {
        alert("You have already rated this item.");
        return prevRatings;
      }
      const updatedRatings = {
        ...prevRatings,
        [itemId]: {
          count: currentRatings.count + 1,
          sum: currentRatings.sum + rating,
          users: [...currentRatings.users, userEmail]
        },
      };

      localStorage.setItem('ratings', JSON.stringify(updatedRatings));

      return updatedRatings;
    });
  };

  const getAverageRating = (itemId) => {
    const currentRatings = ratings[itemId] || { count: 0, sum: 0 };
    return currentRatings.count > 0 ? currentRatings.sum / currentRatings.count : 0;
  };

  const logout = () => {
    auth.signOut().then(() => {
      setUser(null);
      localStorage.removeItem('user');
    }).catch((error) => {
      console.error("Error during sign out:", error);
    });
  };

  return (
    <div>
      {user && <button onClick={logout}>Logout</button>}
      <h1>Contact Information</h1>
      {isLoading ? (
        <h2>Loading...</h2>
      ) : isError ? (
        <h2>Error: {isError}</h2>
      ) : (
        <>
          <div className="dropdown">
            <label htmlFor="category-select">Choose a category:</label>
            <select
              id="category-select"
              value={selectedCategory}
              onChange={handleCategoryChange}
              aria-label="Select Category"
            >
              {myData.map((category) => (
                <option key={category.cat_id} value={category.cat_id.toString()}>
                  {category.cat}
                </option>
              ))}
            </select>
          </div>
          <div className="card-grid">
            {myData.map((category) => (
              <div key={category.cat_id}>
                {category.cat_id.toString() === selectedCategory && (
                  <>
                    <h2 className="category-heading">{category.cat}</h2>
                    <div className="cards">
                      {category.data.map((item, index) => (
                        <div key={index} className="card">
                          {/* Link to ItemDetail with item name as parameter */}
                          <Link to={`/item/${item.name}`}>
                            <h3>{item.name}</h3>
                          </Link>
                          {Array.isArray(item.phone) ? (
                            <p>{item.phone.join(", ")}</p>
                          ) : (
                            <p>{item.phone}</p>
                          )}
                          {item.address && <p>Address: {item.address}</p>}
                          {item.specialization && <p>Specialization: {item.specialization}</p>}
                          {item.website && (
                            <p>
                              Website:{" "}
                              {item.website.map((site, i) => (
                                <span key={i}>
                                  <a href={`http://${site}`} target="_blank" rel="noopener noreferrer">
                                    {site}
                                  </a>
                                  {i < item.website.length - 1 && ", "}
                                </span>
                              ))}
                            </p>
                          )}
                          {item.google_maps && (
                            <p>
                              <a href={item.google_maps} target="_blank" rel="noopener noreferrer">
                                Google Maps Location
                              </a>
                            </p>
                          )}
                          <Rating
                            rating={(ratings[item.name]?.sum / ratings[item.name]?.count) || 0}
                            onRate={(rating) => handleRate(item.name, rating)}
                            numRatings={ratings[item.name]?.count || 0}
                            averageRating={getAverageRating(item.name)}
                          />
                        </div>
                      ))}
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default App;
