import React from 'react';
import { Link } from 'react-router-dom';
import Rating from './Rating'; // Ensure Rating component is in the same directory
import { auth } from '../firebase'; // Import your Firebase authentication instance

const Home = ({
  user,
  logout,
  isLoading,
  isError,
  selectedCategory,
  handleCategoryChange,
  myData,
  selectedData,
  ratings,
  handleRate,
  getAverageRating,
}) => {
  return (
    <>
      {user && <button onClick={logout}>Logout</button>}
      <h1>Contact Information</h1>
      {isLoading ? (
        <h2>Loading...</h2>
      ) : isError ? (
        <h2>Error: {isError}</h2>
      ) : (
        <>
          <div className="dropdown">
            <label htmlFor="category-select">Choose a category:</label>
            <select
              id="category-select"
              value={selectedCategory}
              onChange={handleCategoryChange}
              aria-label="Select Category"
            >
              {myData.map((category) => (
                <option key={category.cat_id} value={category.cat_id.toString()}>
                  {category.cat}
                </option>
              ))}
            </select>
          </div>

          {selectedData && (
            <>
              <h2 className="category-heading">{selectedData.cat}</h2>
              <div className="grid">
                {selectedData.data.map((item, index) => (
                  <div key={index} className="card">
                    <Link to={`/details/${item.name}`}>
                      <h3>{item.name}</h3>
                      {Array.isArray(item.phone) ? (
                        <p>{item.phone.join(', ')}</p>
                      ) : (
                        <p>{item.phone}</p>
                      )}
                      {item.address && <p>Address: {item.address}</p>}
                      {item.specialization && <p>Specialization: {item.specialization}</p>}
                      {item.website && (
                        <p>
                          Website:{' '}
                          {item.website.map((site, i) => (
                            <span key={i}>
                              <a href={`http://${site}`} target="_blank" rel="noopener noreferrer">
                                {site}
                              </a>
                              {i < item.website.length - 1 && ', '}
                            </span>
                          ))}
                        </p>
                      )}
                      {item.google_maps && (
                        <p>
                          <a href={item.google_maps} target="_blank" rel="noopener noreferrer">
                            Google Maps Location
                          </a>
                        </p>
                      )}
                      <Rating
                        rating={(ratings[item.name]?.sum / ratings[item.name]?.count) || 0}
                        onRate={(rating) => handleRate(item.name, rating)}
                        numRatings={ratings[item.name]?.count || 0}
                        averageRating={getAverageRating(item.name)}
                      />
                    </Link>
                  </div>
                ))}
              </div>
            </>
          )}
        </>
      )}
    </>
  );
};

export default Home;
