import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getFirestore, doc, getDoc } from 'firebase/firestore'; //

const DetailedView = () => {
  const { itemId } = useParams();
  const [itemData, setItemData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState('');

  useEffect(() => {
    const fetchItemData = async () => {
      const db = getFirestore();
      const docRef = doc(db, 'items', itemId);
      try {
        const docSnap = await getDoc(docRef);
        if (docSnap.exists()) {
          setItemData(docSnap.data());
        } else {
          setIsError('No such document!');
        }
      } catch (error) {
        setIsError(error.message);
      } finally {
        setIsLoading(false);
      }
    };

    fetchItemData();
  }, [itemId]);

  if (isLoading) return <h2>Loading...</h2>;
  if (isError) return <h2>Error: {isError}</h2>;

  return (
    <div>
      <h2>{itemData.name}</h2>
      {Array.isArray(itemData.phone) ? (
        <p>{itemData.phone.join(", ")}</p>
      ) : (
        <p>{itemData.phone}</p>
      )}
      {itemData.address && <p>Address: {itemData.address}</p>}
      {itemData.specialization && <p>Specialization: {itemData.specialization}</p>}
      {itemData.website && (
        <p>
          Website:{" "}
          {itemData.website.map((site, i) => (
            <span key={i}>
              <a href={`http://${site}`} target="_blank" rel="noopener noreferrer">
                {site}
              </a>
              {i < itemData.website.length - 1 && ", "}
            </span>
          ))}
        </p>
      )}
      {itemData.google_maps && (
        <p>
          <a href={itemData.google_maps} target="_blank" rel="noopener noreferrer">
            Google Maps Location
          </a>
        </p>
      )}

    </div>
  );
};

export default DetailedView;
