import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import axios from 'axios';
import { useAuth0 } from '@auth0/auth0-react';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Header from './components/Header';
import CategoryDropdown from './components/CategoryDropdown';
import ItemCard from './components/ItemCard';
import ItemDetail from './components/ItemDetail';
import './App.css';

const App = () => {
  const [myData, setMyData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [isError, setIsError] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const { user, loginWithRedirect, logout, isAuthenticated } = useAuth0();

  useEffect(() => {
    axios
      .get("https://raw.githubusercontent.com/ayushku634/ayushku634/main/data.json")
      .then((response) => {
        setMyData(response.data);
        if (response.data.length > 0) {
          setSelectedCategory(response.data[0].cat_id.toString());
        }
        setIsLoading(false);
      })
      .catch((error) => {
        setIsError(error.message);
        setIsLoading(false);
      });
  }, []);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleRate = (itemId, rating) => {
    // Handle rating logic here
  };

  const getAverageRating = (itemId) => {
    // Calculate average rating logic here
  };

  const handlePriorityChange = (itemId, newPriority) => {
    setMyData((prevData) => {
      const updatedData = prevData.map((item) =>
        item.name === itemId ? { ...item, priority: newPriority } : item
      );
      // Sort updatedData by priority descending
      updatedData.sort((a, b) => b.priority - a.priority);
      return updatedData;
    });
  };

  return (
    <>
      <Header />
      <Routes>
        <Route path="/" element={
          <>
            <h1>Contact Information</h1>
            {isLoading ? (
              <h2>Loading...</h2>
            ) : isError ? (
              <h2>Error: {isError}</h2>
            ) : (
              <>
                <CategoryDropdown 
                  categories={myData} 
                  selectedCategory={selectedCategory} 
                  handleCategoryChange={handleCategoryChange} 
                />

                <div className="grid">
                  {myData.map((item, index) => (
                    <ItemCard
                      key={index}
                      item={item}
                      handleRate={handleRate}
                      getAverageRating={getAverageRating}
                      handlePriorityChange={handlePriorityChange}
                      isAuthenticated={isAuthenticated}
                    />
                  ))}
                </div>
              </>
            )}
          </>
        } />
        <Route path="/item/:itemName" element={<ItemDetail />} />
      </Routes>
      <ToastContainer />
    </>
  );
};

export default App;
