import React from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import { Navigate } from 'react-router-dom';
//import './AdminPanel.css'; // Custom CSS for the admin panel

const AdminPanel = () => {
  const { user, isAuthenticated } = useAuth0();

  // Check if the user has admin privileges (assuming user object has roles)
  const isAdmin = isAuthenticated && user && user['http://example.com/roles'].includes('admin');

  if (!isAdmin) {
    return <Navigate to="/" />;
  }

  return (
    <div className="admin-panel">
      <h1>Admin Panel</h1>
      {/* Add more admin functionalities here */}
      <p>Welcome, {user.name}! You have admin access.</p>
    </div>
  );
};

export default AdminPanel;
