// App.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth0 } from '@auth0/auth0-react';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const App = () => {
  const [myData, setMyData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [isError, setIsError] = useState("");
  const [isLoading, setIsLoading] = useState(true);
  const { user, loginWithRedirect, logout, isAuthenticated } = useAuth0();
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get("https://raw.githubusercontent.com/ayushku634/ayushku634/main/data.json")
      .then((response) => {
        setMyData(response.data);
        if (response.data.length > 0) {
          setSelectedCategory(response.data[0].cat_id.toString());
        }
        setIsLoading(false);
      })
      .catch((error) => {
        setIsError(error.message);
        setIsLoading(false);
      });
  }, []);

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredData = myData.filter(item => {
    return item.data.some(entry =>
      entry.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  const handleRate = (itemId, rating) => {
    // Implementation for rating function
  };

  // Other functions and JSX remain unchanged

  return (
    <>
      {/* Search Input */}
      <input
        type="text"
        placeholder="Search by name..."
        value={searchTerm}
        onChange={handleSearch}
      />

      {/* Existing JSX code for categories, data mapping, etc. */}
    </>
  );
};

export default App;
